    if (data===undefined ||data.length===0)
    {

        return <div>....</div>;

    }
else{
    return (
        <div className="livescore">
                         <div>
                <h3 style={{textAlign: "center"}} >Live comments !</h3>
              
              <div style={{textAlign: "center"}} style={{marginBottom: "100%"}}className="commentWrapper">
                    
                    <div  class="flex messages">
                        <Table striped bordered hover>
                            <thead>
                                <tr>
                                    <th>Matchs</th>
                                    <th>Description</th>
                                    <th>Date</th>
                                    <th>Edit</th>

                                </tr>
                            </thead>
                            <tbody>
                                {  
                        data.map((comment, i) => (

                               <tr>
                                <td>  <Form.Control value={matchs[i]}  name={"matchs"+i} type="text"/></td>
                                <td> <Form.Control value={description[i]} type="text" name={"description"+i} /></td>
                                <td>  <Form.Control  type="date" name={"date"+i} /></td>
                                    <td>
                                        <button>Update</button>
                                        <button>Delete</button>

                                    </td>
                                </tr>
                                       ))

                               
                               }
                            </tbody>
                        </Table>
                   

                    </div>
              </div>
              
              </div>
        </div>
    );
                            }
}
