import PointsTable from "./PointsTable";
import React, {useState, useEffect} from "react";
import "./App.css";
import Graph from "./Graph";
import Venues from "./Venues";
import LiveScore from "./LiveScore";
import {getStanding, getplayers, getvenue, getMatches, getLive, getOdds, getSingleMatchOdds} from "./Redux/action";
import {connect} from "react-redux";
import  {Card ,ListGroup,Table,Button,Row,Col,Form} from 'react-bootstrap';
import Axios from 'axios';

export default function Admin() {

    const [data, setData] = useState([]);

     const [matchs, setMatchs] = useState([]);
    const [description, setDescription] = useState([]);
    const [date, setDate] = useState([]);
    
    
    function inputUpdate(event) 
    {
        setMatchs({[event.target.name]: event.target.value});
        setDescription({[event.target.name]: event.target.value});
        setDate({[event.target.name]: event.target.value});


      };

    useEffect(() => {

        const fetchData = async () => {

            const response = await Axios.get('http://localhost:5000/comments/', {  
            });
             setData(response.data);
             console.log(response.data);
            
             var matchess=[];
             var descriptionss=[];
             var datess=[];
          
            for (var i=0; i<response.data.length; i++)
            {
                matchess.push((response.data[i].matchs));
                descriptionss.push((response.data[i].description));
                datess.push((response.data[i].date));
            }
            
                setMatchs(matchess);
                setDescription(descriptionss);
                setDate(datess);

          };
          fetchData();
        }, []);

       function updateComment()
       {



       }



    if (data===undefined ||data.length===0)
    {

        return <div>....</div>;

    }
else{
    return (
        <div className="livescore">
                         <div>
                <h3 style={{textAlign: "center"}} >Live comments !</h3>
              
              <div style={{textAlign: "center"}} style={{marginBottom: "100%"}}className="commentWrapper">
                    
                    <div  class="flex messages">
                        <Table striped bordered hover>
                            <thead>
                                <tr>
                                    <th>Matchs</th>
                                    <th>Description</th>
                                    <th>Date</th>
                                    <th>Edit</th>

                                </tr>
                            </thead>
                            <tbody>
                                {  
                        data.map((comment, i) => (

                               <tr>
                                <td>  <Form.Control onChange={inputUpdate} value={matchs[i]}  name={"matchs"+i} type="text"/></td>
                                <td> <Form.Control onChange={inputUpdate} value={description[i]} type="text" name={"description"+i} /></td>
                                <td>  <Form.Control onChange={inputUpdate} value={date[i]} type="date" name={"date"+i} /></td>
                                    <td>
                                        <button  onClick={() => updateComment(comment._id)} >Update</button>
                                        <button onClick={() => deleteComment(comment._id)}>Delete</button>

                                    </td>
                                </tr>
                                       ))

                               
                               }
                            </tbody>
                        </Table>
                   

                    </div>
              </div>
              
              </div>
        </div>
    );
                            }
}
