import PointsTable from "./PointsTable";
import React, {useState, useEffect} from "react";
import "./App.css";
import Graph from "./Graph";
import Venues from "./Venues";
import LiveScore from "./LiveScore";
import {getStanding, getplayers, getvenue, getMatches, getLive, getOdds, getSingleMatchOdds} from "./Redux/action";
import {connect} from "react-redux";
import  {Card ,ListGroup,Table,Button,Row,Col,Form} from 'react-bootstrap';
import Axios from 'axios';

export default function Admin() {

    const [data, setData] = useState([]);

    useEffect(() => {

        const fetchData = async () => {

            const response = await Axios.get('http://localhost:5000/comments/', {  
            })
            .catch((error) => console.log(error.resp));
             setData(response);
          };

          fetchData();
          console.log(data);

    }, []);

    if (data===undefined ||data.length===0)
    {
        return <div>....</div>;

    }
else{

    return (
        <div className="livescore">
                         <div>
                <h3 style={{textAlign: "center"}} >Live comments !</h3>
              
              <div style={{textAlign: "center"}} style={{marginBottom: "100%"}}className="commentWrapper">
                    
                    <div  class="flex messages">
                        <Table striped bordered hover>
                            <thead>
                                <tr>
                                    <th>Matchs</th>
                                    <th>Description</th>
                                    <th>Date</th>
                                    <th>Edit</th>

                                </tr>
                            </thead>
                            <tbody>
                                {  
                        data.data.map((comment, i) => (

                               <tr>
                                <td>  <Form.Control value={comment.matchs} type="text" placeholder="Enter email" /></td>
                                <td> <Form.Control value={comment.description}type="text" placeholder="Enter email" /></td>
                                <td>  <Form.Control value={comment.date} type="date" placeholder="Enter email" /></td>
                                    <td>
                                        <button>Update</button>
                                        <button>Delete</button>

                                    </td>
                                </tr>
                                       ))

                               
                               }
                            </tbody>
                        </Table>
                   

                    </div>
              </div>
              
              </div>
        </div>
    );
                            }
}
